package com.csi.oops;

/**
 * @author DELL
 *
 */
public class BreakWhileEx {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, sum=0;
		for(i=1; i<=10; i++)
		{
			sum=sum+i;
			
		}
		System.out.println("sum of first 10 digits is : "+sum);
		

	}

}
